parameters.inputSchema:
{
  "name": "prospect_search_schema",
  "schema": {
    "type": "object",
    "properties": {
      "JobTitle": {
        "type": "string",
        "description": "The title of the job position of the prospect being searched."
      },
      "CompanyType": {
        "type": "string",
        "description": "The type of company associated with the prospect."
      },
      "Location": {
        "type": "string",
        "description": "The geographical location where the prospect is based."
      },
      "CompanySize": {
        "type": "string",
        "description": "The size category of the company (e.g., number of employees)."
      },
      "Industry": {
        "type": "string",
        "description": "The industry sector the company belongs to."
      },
      "LeadNumber": {
        "type": "number",
        "description": "The number of leads to be generated from this search query."
      }
    },
    "required": [
      "JobTitle",
      "CompanyType",
      "Location",
      "CompanySize",
      "Industry",
      "LeadNumber"
    ],
    "additionalProperties": false
  },
  "strict": true
}

