parameters.inputSchema:
{
  "name": "create_outreach_messages",
  "description": "Generates an array of messages based on an outreach strategy and today's date.",
  "strict": true,
  "parameters": {
    "type": "object",
    "required": [
      "outreach_strategy"
    ],
    "properties": {
      "outreach_strategy": {
        "type": "object",
        "properties": {
          "messages": {
            "type": "array",
            "description": "Array of message objects to be constructed based on the outreach strategy.",
            "items": {
              "type": "object",
              "properties": {
                "subject": {
                  "type": "string",
                  "description": "Subject of the email, leave blank for LinkedIn messages."
                },
                "body": {
                  "type": "string",
                  "description": "The message body in properly spaced markdown format."
                },
                "type": {
                  "type": "string",
                  "enum": [
                    "Linkedin",
                    "Email"
                  ],
                  "description": "Type of the message (LinkedIn or Email)."
                },
                "date": {
                  "type": "string",
                  "description": "Date when to execute the specific message."
                }
              },
              "additionalProperties": false,
              "required": [
                "subject",
                "body",
                "type",
                "date"
              ]
            }
          }
        },
        "execution_criteria": {
          "type": "object",
          "properties": {
            "frequency": {
              "type": "string",
              "description": "How often to send messages."
            },
            "time_of_day": {
              "type": "string",
              "description": "Preferred time of day to send messages."
            }
          }
        },
        "additionalProperties": false,
        "required": [
          "messages"
        ]
      }
    },
    "today_date": {
      "type": "string",
      "format": "date",
      "description": "Today's date to reference for message scheduling."
    },
    "additionalProperties": false
  }
}
